INSERT INTO `food` (`id`, `foodName`, `categoryId`, `price`, `foodDescription`, `foodImage`, `createDate`, `updateDate`) VALUES
(2, 'Bắp cải', 3, 12500, '<p>Là một loại thực phẩm vô cùng quen thuộc, rất dễ mua và chế biến thành nhiều món ăn đa dạng khác nhau trong bữa cơm hằng ngày. Bắp cải trắng đặc biệt mang đến lợi ích trong việc hỗ trợ phòng chống ung thư, giúp cơ thể khỏe mạnh toàn diện. </p><p>\r\nCác món có thể chế biến từ Bắp Cải: </p><p>\r\n-Ăn sáng với trứng chiên bắp cải </p><p>\r\n-Mì tôm xào bắp cải, trứng đơn giản ngon miệng </p><p>\r\n-Ăn chay cùng bắp cải cuộn rau củ </p><p>\r\n-Gà xào bắp cải phô mai kiểu Hàn Quốc </p><p>\r\n-Canh bắp cải thanh mát </p>', 'bapcai.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(3, 'Bắp mỹ', 3, 17000, '<p>Bắp Mỹ là một loại thực phẩm được trồng rất nhiều ở khắp nơi trên thế giới. Đây là một loại quả vừa ngon, lại có rất nhiều chất khoáng chất và vitamin. Bắp có thể chế biến thành nhiều món ăn ngon như: cơm bắp, chè bắp, sữa bắp,... bất kỳ món gì cũng tạo nên hương vị tuyệt hảo. </p><p>\r\nCác món có thể chế biến từ Bắp Mỹ: </p><p>\r\n- Cơm bắp thơm ngọt. </p><p>\r\n- Chè bắp ngọt ngon. </p><p>\r\n- Bắp luộc nhanh chóng tiện lợi. </p><p>\r\n- Bánh phồng tôm nhân bắp đậu cà rốt lạ miệng. </p><p>\r\n- Súp tôm bắp ngon ngất ngây. </p><p>\r\n- Sữa bắp dinh dưỡng</p>', 'bapmy.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(4, 'Bầu', 3, 11000, '<p>Được nhiều chị em nội trợ chọn mua để chế biến thành các món ăn ngon cho gia đình. Ngoài làm thực phẩm, bầu sao còn có thể dùng trong đông y, có tác dụng lợi tiểu, mát gan, giải độc, làm đẹp da và giảm cân. Bầu có thể chế biến thành các món ăn như: luộc, xào, nấu canh đều rất ngon. </p><p>\r\nCác món có thể chế biến từ Bầu: \r\n- Bầu luộc chấm muối vừng, mát và bổ.</p><p>\r\n- Bầu nấu tôm thanh mát.</p><p>\r\n- Canh bầu nấu thịt.</p>', 'bau.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(5, 'Bí đỏ', 3, 21000, '<p>Còn gọi là bí đỏ hạt đậu, đây là giống bí có ruột rất đặc, ít hả ăn dẻo và ngọt. Bí hồ lô chứa nhiều chất dinh dưỡng và mang lại nhiều lợi ích cho sức khoẻ. Bí hồ lô có thể chế biến thành nhiều món ăn ngon như: sữa bí, canh bí, súp bí,... món nào cũng đều thơm ngon. </p><p>\r\n- Các món có thể chế biến từ Bí Đỏ:</p><p>\r\n- Tăng cân với sữa bí đỏ hạt sen.</p><p>\r\n- Canh bí đỏ hầm xương bổ dưỡng cho cả nhà.</p><p>\r\n- Súp bí đỏ kem tươi béo ngậy.</p><p>\r\n- Bổ não cùng với món óc heo chưng bí đỏ.</p><p>\r\n- Cháo gà bí đỏ bổ dưỡng cho bé cưng nhà bạn.</p>', 'bidoholo.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(6, 'Bí xanh', 3, 14000, '<p>Được nhiều chị em nội trợ chọn mua để chế biến thành các món ăn ngon cho gia đình. Ngoài làm thực phẩm, bí xanh còn có thể dùng trong đông y, có tác dụng lợi tiểu, mát gan, giải độc, làm đẹp da và giảm cân. Bầu có thể chế biến thành các món ăn như: luộc, xào, nấu canh đều rất ngon. </p><p>\r\nCác món có thể chế biến từ Bí Xanh:.</p><p>\r\n- Trà bí đao hạt chia.</p><p> \r\n- Canh bí đao thịt bằm.</p><p> \r\n- Canh bí đao nhồi thịt.</p><p> \r\n- Mứt bí đao đường phèn.</p>', 'bixanh.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(7, 'Bông súng', 3, 13000, '<p>Bông súng - đặc sản miền Tây có mùi thơm, nhiều màu: màu trắng, màu tím lục bình, màu hồng cánh sen. Thân bông súng có màu nâu. Vị giòn, ngọt và thơm nên thường người sử dụng thân bông súng để chế biến món ăn như gỏi, lẩu,... </p><p>\r\nCác món có thể chế biến từ Bông Súng:.</p><p>\r\n- Làm rau sống.</p><p>\r\n- Bông súng bóp xổi.</p><p>\r\n- Gỏi bông súng.</p><p>\r\n- Bông súng xào.</p><p>\r\n- Canh chua, lẩu chua bông súng.</p><p>\r\n- Bông súng muối chua.</p><p>\r\n- Gỏi bông súng tôm thịt.</p>', 'bongsung.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(8, 'Cà chua bi', 3, 8000, '<p>Cà chua bi là loại rau củ rất tốt cho sức khoẻ, đây là giống cà có kích thước khá nhỏ nhưng cũng rất dồi dào chống dinh dưỡng như chất xơ, vitamin C,... Bạn có thể sử dụng cà chua bi để ăn sống, trộn salad hoặc có thể dùng để chế biến thành các món ăn khác đều rất tốt.</p><p>\r\nCác món có thể chế biến từ Cà Chua Bi:.</p><p> \r\n- Xiên cùng với thịt heo, thịt bò nướng, hải sản nướng và các loại củ khác..</p><p>\r\n- Canh chua cá tra chua cay, đúng vị miền Tây.</p><p>\r\n- Giảm cân cùng nước ép, sinh tố cà chua.</p>', 'cachuabi.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(9, 'Cải bó xôi', 3, 32500, '<p>Với công dụng tuyệt vờicó thể kể đến như chống ung thư, chống viêm, ngăn ngừa bệnh tuyến tiền liệt, hỗ trợ giảm cân, làm đẹp da, sáng mắt,.... Cải bó xôi của Bách hóa xanh tự tin mang đến cho bạn những món ăn đầy đủ dinh dưỡng, hấp dẫn và thanh mát.</p><p>\r\nCác món có thể chế biến từ Cải bó xôi:.</p><p>\r\n- Canh cải bó xôi nấu tôm, nấu với thịt băm.</p><p>\r\n- Cháo cá hồi cải bó xôi.</p><p>\r\n- Cải bó xôi xào thịt bằm,....</p>', 'caiboxoi.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(10, 'Cải ngọt', 3, 18800, '<p>Với vị ngọt thanh, phù hợp với việc chế biến nhiều món ăn khác nhau nên Cải ngọt baby được sử dụng phổ biến trong các bữa ăn của người Việt. Do chưa hàm lượng dinh dưỡng cao nên cải ngọt giúp phòng ngừa ung thư, hỗ trợ trị bệnh gout,....</p><p>\r\nCác món có thể chế biến từ Cải Ngọt:.</p><p>\r\nCải ngọt có vị ngọt thanh tự nhiên, vì thế chúng thường được chế biến thành các món canh để tạo ra vị thanh đạm như canh cải ngọt thịt bằm, canh cải ngọt hến,....</p><p>\r\nKhi kết hợp cải ngọt với các loại nấm, thịt heo, thịt bò, hải sản,... sẽ tạo ra những món xào đơn giản như rất thơm ngon và bổ dưỡng như cải ngọt xào tỏi, cải ngọt xào nấm bào ngư,....</p><p>\r\nĐặc biệt, cải ngọt là một trong những nguyên liệu không thể thiếu để tạo ra những món mì xào giòn hải sản, mì xào thịt bò,... thơm ngon..</p>', 'caingot.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(11, 'Cải thìa', 3, 6900, '<p>Cải thìa còn có tên là bạch giới tử thuộc họ cải cùng họ với cải thảo, cải bẹ xanh. Đây là loại rau chứa nhiều thành phần dinh dưỡng dễ chế biến, ăn ngon miệng. Đặc biệt, rau cải thìa cũng mang đến nhiều lợi cho sức khỏe như phòng ngừa bệnh đục nhân mắt, ngăn ngừa ung thư,...</p><p>\r\nCác món có thể chế biến từ Cải Thìa:.</p><p>\r\nCanh cải thìa với tôm.</p><p>\r\nCải thìa xào thịt nạc.</p>', 'caithia.jpeg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(12, 'Cần tây', 3, 14700, '<p>Cần tây là loại rau quen thuộc trong bữa ăn hàng ngày, không chỉ giúp món ăn thêm phần thơm ngon mà còn mang lại nhiều lợi ích cho sức khỏe. Theo chuyên gia dinh dưỡng Megan Ware, trong cần tây chứa gần 95% nước cùng nhiều vitamin, protein và chất khoáng. Đặc biệt, hàm lượng vitamin K trong cần tây rất cao. Bên cạnh đó, cần tây chứa hàm lượng chất xơ phong phú, giúp ích cho quá trình đào thải độc tố ra ngoài cơ thể.</p><p>\r\nCác món có thể chế biến từ Cần tây:.</p><p>\r\n- Detox ngon miệng với nước ép cần tây táo xanh.</p><p>\r\n- Da đẹp với nước ép cần tây, cà rốt và táo.</p><p>\r\n- Mực xào cần tây đơn giản thơm ngon.</p><p>\r\n- Thịt bò xào cần tây dinh dưỡng.</p><p>\r\n- Sinh tố cần tây cực dễ uống.</p>', 'cantay.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(13, 'Cà rốt', 3, 14000, '<p>Cà rốt không chỉ là loại củ quen thuộc trong các món ăn trong gia đình mà còn là vị thuốc quý, rất tốt cho sức khỏe. Với hàm lượng chất dinh dưỡng và vitamin A cao, cà rốt được xem là một nguyên liệu cần thiết cho các món ăn dặm của trẻ nhỏ, giúp trẻ sáng mắt và cung cấp nguồn chất xơ dồi dào.</p><p>\r\nNgoài ra, cà rốt còn được xem là một \"thần dược\" trong quá trình chăm sóc da của phụ nữ. Chỉ với những bước làm đơn giản là bạn đã có ngay hỗn hợp mặt nạ cà rốt - mật ong giúp ngăn ngừa mụn, làm sáng da và cải thiện làn da sạm và lão hóa.</p><p>\r\nCác món có thể chế biến từ Cà rốt:.</p><p>\r\n- Cà rốt có thể chế biến thành nước ép cà rốt cam và củ dền, nước ép cà rốt và cà chua, sinh tố cà rốt. Ngoài ra, cà rốt cũng có thể làm cà rốt, củ cải chua ăn kèm với thịt nguội, chả lụa cho các bữa tiệc. Một số món ăn từ cà rốt như sau: Các loại cháo bổ dưỡng cho trẻ như cháo tim heo cà rốt, cháo lươn cà rốt, cháo thịt bò cà rốt,...</p><p>\r\n- Cà rốt dùng cho các món nộm gỏi như nộm su hào cà rốt hoặc làm ra món kim chi cải thảo truyền thống của Hàn Quốc,...</p><p>\r\n- Cà rốt xào với mì, nui,... cùng với các nguyên liệu thịt heo, thịt bò, trứng,... Đặc biệt, cà rốt còn được xem là món ăn vặt an toàn khi được chế biến thành mứt cà rốt, thích hợp cho việc nhâm nhi của cả gia đình.</p>', 'carot.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(14, 'Cà tím', 3, 25500, '<p>Cà tím hay còn được gọi là cà dái dê, đây là một loại rau củ chế biến được thành rất nhiều món ăn thơm ngon như: hấp, xào, nướng, ăn sống,... mỗi dạng đều mang lại hương vị rất ngon. Trong cà tím chứa hàm lượng chất xơ vô cùng cao và giàu sắt rất tốt cho cơ thể.</p><p>\r\nCà tím có tên gọi khác là cà dái dê, có họ hàng với cà chua, khoai tây, cà pháo. Loại củ, quả này sử dụng trong chế biến dưới dạng thức ăn hầm, xào, nướng. Nhờ chứa hàm lượng chất xơ vô cùng cao và giàu sắt, cà tím còn giúp giảm nguy cơ mắc các bệnh ung thư như ung thư ruột kết, trực tràng, tim mạch, chữa chứng hay quên,...</p><p>\r\nCác món có thể chế biến từ Cà tím:</p><p>\r\n- Cà tím chiên tẩm nước tương.</p><p>\r\n- Cà tím nhồi thịt, sốt chua ngọt.\r\n- Thanh ngọt, bùi béo với cà tím hấp mỡ hành,...</p>', 'catim.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(15, 'Củ cải đỏ', 3, 37500, '<p>Củ cải đỏ hay củ cải đường có vị ngọt mát, thanh đạm, có lớp vỏ ngoài mỏng, màu đỏ hồng. Bên trong có phần thịt giòn xốp màu trắng trong và củ có dạng tròn. Củ cải đỏ còn chứa rất nhiều chất dinh dưỡng, vitamin rất tốt cho cơ thể, giúp giảm cân hiệu quả, lành mạnh.</p><p>\r\nCủ cải đỏ (hay còn gọi là củ cải đường) có vị ngọt mát, thanh đạm, là nguyên liệu không thể thiếu cho những thực đơn giảm cân lành mạnh và hiệu quả. Đồng thời, loại củ này cũng chứa rất nhiều vitamin và khoáng chất cần thiết cho cơ thể.</p><p>\r\nKhông giống với cà rốt và củ cải trắng, củ cải đỏ có lớp vỏ ngoài mỏng, màu đỏ hồng. Bên trong có phần thịt giòn xốp màu trắng trong và củ có dạng tròn.</p><p>\r\nCác món có thể chế biến từ Củ cải đỏ:.</p><p>\r\n- Nộm củ cải đỏ – Món ăn ngon với củ cải đỏ.</p><p>\r\n- Củ cải đỏ hầm cánh gà – Món ăn ngon với củ cải đỏ.</p><p>\r\n- Súp củ cải – món ăn đặc trưng của người ukraine.</p><p>\r\n- Súp củ cải đỏ là một món ăn đặc biệt.</p>', 'cucaido.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(16, 'Củ cải trắng', 3, 13500, '<p>Củ cải trắng là một loại rau củ vừa có thể dùng để làm thực phẩm, vừa có thể sử dụng để làm dầu hạt cải. Loại rau củ này chứa rất nhiều vitamin và khoáng chất, rất tốt cho cơ thể. Một vài công dụng của củ cải trắng có thể kể đến như: hỗ trợ giảm cân, hỗ trợ hệ tiêu hoá, tăng cường miễn dịch,...</p><p>\r\nCủ cải trắng hay còn được gọi là củ cải mùa đông, có nguồn gốc từ Trung Quốc và Nhật Bản. Đây là một loại rau củ được trồng để làm thực phẩm và dầu hạt. Củ cải trắng có ngoại hình giống với cà rốt nhưng có màu trắng, mùi vị thì hơi ngọt và cay.</p><p>\r\nCác món có thể chế biến từ Củ Cải Trắng:</p><p>\r\nCủ cải trắng có thể chế biến thành nhiều món ăn ngon có thể kể đến như: Thịt kho củ cải, bò kho củ cải, cá kèo kho củ cải trắng,... Đặc biệt, củ cải trắng còn là nguyên liệu không thể thiếu trong việc chế biến thành các món kim chi Hàn Quốc hay kim chi Việt Nam từ củ sen giòn.</p>', 'cucaitrang.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(17, 'Củ dền', 3, 17500, '<p>Là một loại củ thường được các chị em nội trợ chọn vào menu hàng tuần. Bên trong loại rau củ này và cả lá của nó có chứa nhiều vitamin và khoáng chất giúp cải thiện sức khoẻ cho cơ thể. Củ dền có thể dùng chế biến thành các món ăn như canh hay có thể dùng để làm nước ép cũng rất ngon..</p><p>\r\nCủ dền hay còn gọi là củ dền đỏ, là một loại củ quen thuộc trong món ăn của nhiều gia đình. Củ dền có lớp vỏ bên ngoài xù xì, bên trong có màu đỏ thẫm hoặc tím than, lớp thịt cứng giòn. Đây là loại rau củ chứa nhiều vitamin (A, B2, C,...) cùng với hàm lượng chất xơ, chất sắt cao và các khoáng chất có lợi.\r\nVì thế, củ dền có rất nhiều công dụng đối với sức khỏe con người khi chế biến thành nhiều món ăn như điều hòa huyết áp, cải thiện đường tiêu hóa, hỗ trợ thải độc,....</p><p>\r\nCác món có thể chế biến từ Củ Dền:.</p><p>\r\n- Củ dền sau khi sơ chế được nấu kết hợp với nhiều nguyên liệu khác nhau như thịt heo, rau củ quả các loại..</p><p>\r\n- Canh củ dền với sườn heo hoặc thịt heo bằm thơm ngon, bổ dưỡng.</p><p>\r\n- Nước ép củ dền giúp bổ máu, ổn định huyết áp..</p><p>\r\n- Súp củ dền cho trẻ.</p>', 'cuden.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(18, 'Đậu bắp', 3, 13800, '<p>Chứa nhiều chất xơ, giàu dinh dưỡng, nhiều vitamin,…Đậu bắp của Bách Hóa Xanh luôn rất được ưa chuộng để chế biến những món ăn hàng ngày. Những thành phần dưỡng chất trong đậu bắp rất hữu ích cho hệ tiêu hóa, tim mạch, chống ung thư, tốt cho da và mắt,...</p><p>\r\nĐậu bắp với tên gọi khác mướp tây hay bắp còi, là một trong loại thực phẩm quen thuộc được sử dụng trong các bữa ăn hằng ngày. Nó chứa nhiều chất xơ, giàu dinh dưỡng, nhiều vitamin,…đều là những thành phần dưỡng chất hữu ích cho hệ tiêu hóa, tim mạch, chống ung thư, tốt cho da và mắt,...\r\nNgoài việc trở thành món ăn trong các bữa ăn hằng ngày thì đậu bắp còn được chế biến thành nước để uống. Nước đậu bắp rất tốt cho các bà bầu, hạn chế khả năng dị tật của thai nhi tốt.</p><p>\r\nCác món có thể chế biến từ Đậu Bắp:.</p><p>\r\nNgoài món luộc thì đậu bắp còn được chế biến nhiều món ngon đa dạng như:.</p><p> \r\n- Giải nhiệt mát người với canh chua đậu bắp.</p><p>\r\n- Đậu bắp muối chua.</p><p>\r\n- Ngon bổ dưỡng với đậu bắp xào thịt bò.</p><p>\r\n- Đậu bắp xào bún khô đơn giản, thanh tịnh.</p>', 'daubap.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(21, 'Gừng', 3, 4800, '<p>Gừng là một loại củ có rất nhiều công dụng đối với chúng ta. Ngoài là gia vị trong nấu ăn hàng ngày, chúng còn có thể dùng trong làm đẹp, làm thuốc cũng cực tốt. Có thể kể đến một số lợi ích của gừng như: làm ấm cơ thể, trừ hàn, tiêu đàm, dịu ho, cầm máu,...</p><p>\r\nGừng là một loài thực vật hay được dùng làm gia vị, thuốc. Trong củ gừng có các hoạt chất: Tinh dầu zingiberen, chất nhựa, chất cay, tinh bột. Chính tinh dầu này làm món ăn thơm ngon hơn, khử đi mùi tanh của thịt cá. Không những vậy, gừng có vị cay, tính ôn, kích thích vị giác, tạo cảm giác thèm ăn,\r\nkhiến bữa ăn thêm ngon miệng. Bên cạnh đó những lợi ích từ gừng như: làm ấm cơ thể, trừ hàn, tiêu đàm, dịu ho, cầm máu, giúp giảm các cơn đau cơ, viêm khớp, thấp khớp, đau đầu hay đau nửa đầu.</p><p>\r\nCác món có thể chế biến từ Gừng:.</p><p>\r\n- Ấm nồng ngày mưa với gà kho gừng.</p><p>\r\n- Canh cải bẹ xanh nấu gừng ấm bụng, giải cảm.</p><p>\r\n- Ngon lạ miệng với đuôi bò hấp gừng.</p><p>\r\n- Mực nhồi thịt hấp gừng.</p><p>\r\n- Ngoài ra, gừng có thể dùng để pha trà nóng, mật ong hoặc làm nước mắm chấm,...</p>', 'gung.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(22, 'Hành lá', 3, 9000, '<p>Hành lá hay gọi là hành hoa, hành hương, hành ta, là loại rau nêm có mùi thơm đặc trưng, được dùng chủ yếu như một loại gia vị ăn kèm của các món ăn. Ngoài ra, hành lá cũng xuất hiện trong một số bài thuốc đông y nhằm phòng hoặc chữa một vài căn bệnh nào như:\r\ngiải quyết dứt điểm triệu chứng cảm sốt, nhức đầu, ăn uống không tiêu, ngăn ngừa tiểu đường, tốt cho mắt, nâng cao hoạt động hệ tim mạch,....</p><p>\r\nCác món có thể chế biến từ Hành lá:.</p><p>\r\n- Chân gà hấp hành.</p><p>\r\n- Trứng chiên thịt bằm hành lá.</p><p>\r\n- Bánh hành giòn tan.</p><p>\r\n- Làm gia vị cho các món canh: canh bí đỏ, bắp cải...</p>', 'hanhla.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(23, 'Hành tây', 3, 17000, '<p>Hành tây là một loại củ phổ biển được sử dụng trong rất nhiều món ăn của người Việt và trên toàn thế giới. Loại rau củ này chứa khá nhiều vitamin và khoáng chất có công dụng cho sức khỏe. Hành tây có thể dùng để chiên, xào, nướng hoặc có thể ăn sống đểu rất ngon.</p><p>\r\nHành tây là loại củ mọc dưới lòng đất, được trồng phổ biến trên toàn thế giới và có quan hệ gần với hẹ, tỏi và hành lá. Đây là nguyên liệu chủ yếu trong nhiều món ăn, được chế biến rất đa dạng, từ nướng, luộc, chiên, rang, xào, lăn bột hoặc thậm chí là ăn sống. Hành tây chứa khá nhiều vitamin và khoáng chất có công dụng cho sức khỏe như: \r\ngiúp điều hòa đường huyết, cải thiện sức khỏe xương, ngăn ngừa ung thư, tăng cường hệ miễn dịch,...</p><p>\r\nCác món có thể chế biến từ Hành tây:.</p><p>\r\n- Gà xào hành tây lạ miệng, đưa cơm.</p><p>\r\n- Giòn thơm với mực xào hành tây.</p><p>\r\n- Bò xào hành tây thơm mềm, thơm ngon.</p><p>\r\n- Súp hành tây kiểu Pháp.</p>', 'hanhtay.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(24, 'Khoai lang', 3, 29000, '<p>Là món ăn được rất nhiều người yêu thích, được trồng và có củ quanh năm, ngon nhất là khi được nướng lên trên một bếp than đổ hồng. Loại củ này có vị ngọt ngào như mật, tan tan trên đầu lưỡi. Khoai lang Nhật chứa nhiều vitamin A, B, C và nhiều khoáng chất cần thiết cho làn da đẹp.</p><p>\r\nLà một loại củ có hình dáng thon, dài. Với lớp vỏ bên ngoài màu tím, trong ruột thì vàng, hương vị ngọt dịu nhẹ, bùi nên chiếm được rất nhiều cảm tình của mọi người. Trong Đông y củ khoai lang có vị ngọt, tính bình, có công dụng nhuận tràng, bồi bổ cơ thể, tiêu viêm, lợi mật, sáng mắt,.. đặc biệt ăn khoai vào buổi sáng sẽ giúp bạn cung cấp đầy đủ dinh dưỡng cho cơ thể, \r\nđặc biệt là chữa được nhiều bệnh nguy hiểm mà bạn không ngờ tới.</p><p>\r\nCác món có thể chế biến từ Khoai lang:.</p><p>\r\nKhoai lang có thể chế biến thành một số món ăn ngon như: khoai lang luộc, khoai lang nướng, khoai lang kén, khoai lang chiên giòn, thạch rau câu khoai lang, chè khoai lang,...</p>', 'khoailang.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(25, 'Khoai mỡ', 3, 47000, '<p>Là nguyên liệu khá quen thuộc của các chị em khi nấu ăn cho cả gia đình. Với hàm lượng khoáng chất và vitamin có trong khoai mỡ sẽ giúp cải thiện hệ tiêu hoá, giúp nhuận tràng, chữa táo bón rất tốt. Khoai mỡ có thể sử dụng để chế biến thành các món như: canh, bánh khoai mỡ, khoai mỡ chiên giòn,...</p><p>\r\nCác món có thể chế biến từ Khoai Mỡ:.</p><p> \r\n- Canh khoai mỡ thịt bằm.</p><p>\r\n- Khoai mỡ nấu với tôm khô.</p><p> \r\n- Bánh khoai mỡ hấp nước cốt dừa.</p><p>\r\n- Khoai mỡ chiên giòn để các bé ăn vặt.</p>', 'khoaimo.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(27, 'Khoai tây', 3, 15000, '<p>Đã quá quen thuộc với mỗi chúng ta. Loại củ này được xuất hiện thường xuyên trên mâm cơm này có rất nhiều công dụng hữu ích.\r\nNó không chỉ tốt cho sức khỏe, làm đẹp hiệu quả mà còn có rất nhiều tác dụng bổ ích khác. Khoai tây bi có thể chế biến thành canh, súp, hoặc chiên đều rất ngon.</p><p>\r\nKhoai tây thuộc họ cà, là một loại củ đa năng có hàm lượng chất dinh dưỡng cao, vì vậy nhiều hộ gia đình tại Việt Nam đã lựa chọn khoai tây như một món ăn chính trong các bữa ăn hàng ngày.\r\nSở hữu nguồn vitamin và khoáng chất phong phú, khoai tây mang lại nhiều lợi ích cho sức khỏe như kháng viêm, giảm đau, tăng cường hệ miễn dịch, kích thích tiêu hóa,...&nbs\r\nCác món có thể chế biến từ Khoai Tây:.</p><p>\r\n- Súp gà khoai tây nhẹ nhàng với bữa sáng.</p><p>\r\n- Bổ dưỡng, giảm cân với salad khoai tây.</p><p>\r\n- Bánh khoai tây nướng phô mai nóng hổi, giòn rụm.</p><p>\r\n- Lạ vị với khoai tây chiên bơ.</p>', 'khoaitay.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(29, 'Nấm đùi gà', 3, 27500, '<p>Nấm đùi gà của Bách hóa Xanh được nuôi trồng và đóng gói theo những tiêu chuẩn nghiêm ngặt, bảo đảm các tiêu chuẩn xanh - sach, chất lượng và an toàn với người dùng. Nấm giòn dai, ngọt thịt, nhiều dinh dưỡng thường được dùng cho các món xào,\r\nchiên giòn hoặc nướng ăn kèm với các loại xốt chấm.</p><p>\r\nNấm đùi gà còn được biết đến với tên gọi khác là nấm bào ngư Nhật. Đây là loại nấm thuộc họ bào ngư, chứa hàm lượng dinh dưỡng lớn, rất tốt cho sức khỏe con người. Chứa nhiều loại vitamin thiết yếu cho cơ thể như: vitamin B1, B6, E,... các dưỡng chất này giúp cải thiện sức khỏe, nâng cao sức đề kháng cho người dùng,\r\nđặc biệt chống ung thư rất hiệu quả, giảm bệnh tiểu đường và tốt cho tiêu hóa.</p><p>\r\nCác món có thể chế biến từ Nấm Đùi Gà:.</p><p>\r\n- Nấm đùi gà sốt sa tế, kho nước tương.</p><p>\r\n- Nấm luộc chấm mắm.</p><p>\r\n- Lẩu nấm kết hợp các loại nấm cực ngon.</p>', 'namduiga.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(30, 'Nấm hải sản', 3, 25000, '<p>Nấm hải sản của Bách hóa Xanh được nuôi trồng và đóng gói theo những tiêu chuẩn nghiêm ngặt, bảo đảm các tiêu chuẩn xanh - sach, chất lượng và an toàn với người dùng. Nấm trắng ngần, ngọt, chứa nhiều chất, hàm lượng dinh dưỡng cao nên thường dùng để nấu cháo hoặc làm rau nhúng lẩu.</p><p>\r\nNấm hải sản còn có tên gọi khác nấm bạch tuyết. Đây được xem là một loại thực phẩm rất tốt cho sức khỏe bởi nó chứa rất nhiều vitamin và protein. Thực phẩm này còn có rất nhiều tác dụng trong việc ngăn ngừa ung thư, thông gan, tốt cho dạ dày, tăng cường trí lực, giảm quá trình lão hóa, tăng cường hệ miễn dịch của cho mẹ bầu.</p><p>\r\nCác món có thể chế biến từ Nấm Hải Sản:.</p><p>\r\n- Gói nấm mua về cắt bỏ gốc.</p><p>\r\n- Rửa sạch với nước.</p><p>\r\n- Cắt khúc to, nhỏ tùy theo món ăn bạn chế biến.</p>', 'namhaisan.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(31, 'Nấm kim châm', 3, 16000, '<p>Nấm là loại thực phẩm tuy có hàm lượng vitamin và khoáng chất chỉ ngang bằng các loại rau xanh nhưng nó lại chứa nhiều loại chất dinh dưỡng cần thiết cho cơ thể trong đó có vitamin D là dưỡng chất mà khó tìm thấy trong rau hoặc một số loại thực phẩm khác. Nấm kim châm cũng là một trong số những nguồn vitamin,\r\nkhoáng chất họ nấm, theo nghiên cứu chỉ với 65g nấm kim châm cung cấp chất dinh dưỡng cần thiết cho cơ thể.</p><p>\r\nCác món có thể chế biến từ Nấm kim châm:.</p><p>\r\n- Canh nấm kim châm nấu thịt bằm.</p><p>\r\n- Canh thịt bò nấm kim châm.</p><p>\r\n- Ba chỉ bò cuộn nấm kim châm.</p><p>\r\n- Cải bó xôi sốt nấm kim châm.</p><p>\r\nHay mua thêm một số loại nấm khác như nấm bào ngư, nấm hải sản, nấm hương,... để có thể tự chế biến món lẩu nấm thơm ngon.</p>', 'namkimcham.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(32, 'Ngò rí', 3, 6300, '<p>Là một loại rau thường được ăn sống, chấm nước kho hoặc dùng để làm gia vị cho các món ăn. Không những là một loại rau ngon, mà ngò rí còn chứa nhiều vitamin A, C có lợi cho hệ miễn dịch của cơ thể. Ngoài ra, nước ép ngò rí còn có tác dụng cực tốt có thể làm hạ sốt, trị cảm cúm hiệu quả.</p><p>\r\nNgò rí hay còn gọi là ngò, rau mùi, ngò suôn, mùi ta,... có mùi thơm, thường được trồng làm rau nêm và gia vị. Kết hợp ăn rau khác nhau tương ứng với từng món ăn khác nhau làm gia tăng hương vị cho món ăn. Hoặc được sử dụng như một phương pháp điều trị bệnh: Rau mùi có chứa nhiều vitamin A, C có lợi cho hệ miễn dịch của cơ thể, uống nước rau mùi có thể làm hạ sốt, trị cảm cúm, bảo vệ tim mạch, lọc máu,...</p><p>\r\nCác món có thể chế biến từ Ngò Nhí:.</p><p>\r\nRau thường được dùng cho hai mục đích chính:.</p><p>\r\n- Ăn sống, húng có tinh dầu cay nên hay được phối trộn với các loại rau mát khác như giá đỗ, bắp chuối thái mỏng, xà lách, rau má.</p><p>\r\n- Gia vị cho các món ăn: nhiều loại rau thơm được gia vào các món ăn như một thứ gia vị, tùy theo nguyên liệu chính để chế biến món ăn mà người nội trợ thường có kinh nghiệm riêng cho các loại rau thơm khác nhau như: bún đậu mắm tôm, bún bò Huế, bún riêu cua, bún chả, lòng lợn, phở cuốn,...</p>', 'ngonhi.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(33, 'Ớt hiểm', 3, 7500, '<p>Với vị cay nồng, thơm, kích thích vị giác của người ăn, ớt là một trong những gia vị không thể thiếu trong nấu ăn cũng như mâm cơm của người Việt. Ớt hiểm của Bách Hóa Xanh luôn giữ được độ tươi mỗi ngày, được nuôi trồng theo quy trình nghiêm ngặt, bảo đảm các chỉ tiêu về an toàn và chất lượng.</p><p>\r\nỚt mang một vị cay đặc trưng, cùng với hành, tỏi, và các loại rau nêm khác trở thành nguyên liệu không thể thiếu trong mỗi món ăn, giúp các món ăn cay nồng, dậy mùi thêm phần hấp dẫn. Người ta có thể dùng ớt ở bất kể món ăn nào nếu muốn có thêm vị cay cay the the, có người còn ăn ớt sống, mỗi bữa cơm hai trái như thói quen để kích thích vị giác, giúp ăn ngon miệng hơn. Ớt có nhiều loại, \r\nnhưng phải nói là cay nhất thì chỉ có ớt hiểm, chính là loại quả ớt thường mọc quả có 3 màu trắng, đỏ, vàng trên cùng một cây.</p><p>\r\nCác món có thể chế biến từ Ớt:.</p><p>\r\n- Bánh mì nướng muối ớt.</p><p>\r\n- Tôm nướng muối ớt.</p><p>\r\n- Gà kho xả ớt.</p><p>\r\n- Bò xào xả ớt.</p><p>\r\n- Canh khổ qua cà ớt.</p><p>\r\n- Lẩu gà ớt hiểm.</p>', 'ot.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(34, 'Rau dền', 3, 11500, '<p>Rau dền tươi xanh đảm bảo chất lượng an toàn thực phẩm. Đặt hàng giao ngay trong ngày tại Green Food.</p><p>\r\nRau dền là một loại rau xanh có 3 loại phổ biến: Rau đền đỏ, rau dền gai, rau dền cơm. Được các bà nội trợ bổ sung trong thực đơn mỗi ngày. Ngoài vị ngọt mát và thành phần dinh dưỡng cao, rau dền xanh còn được xem là một vị thuốc dân gian chữa được nhiều bệnh với lợi ích như:\r\nChống táo bón, điều trị tăng huyết áp, tốt cho bệnh nhân tiểu đường, ngừa ung thư...</p><p>\r\nCác món có thể chế biến từ Rau dền:.</p><p>\r\n- Thanh mát, dễ làm với canh rau dền thịt băm, canh tôm rau dền.</p><p>\r\n- Cháo tôm rau dền rong biển cho bé.</p><p>\r\n- Mì somen rau dền thịt heo.</p><p>\r\n- Nấm xào lòng heo rau dền.</p>', 'rauden.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(35, 'Rau muống', 3, 5500, '<p>Rau muống nước baby là một loại rau muống có phần lá với kích thước nhỏ nhưng công dụng không thua kém gì so với rau muống thông thường. Đặc biệt, đây là loại rau có chứa hàm lượng nước cao (92%) cùng với một số các khoáng chất có lợi khác (canxi, photpho, sắt,...) \r\ncùng các nhóm vitamin B, C,... Vì thế, rau muống có nhiều công dụng như cung cấp nguồn nước cho cơ thể, giúp giải nhiệt, ổn định huyết áp, ngăn ngừa ung thư, điều hòa tim mạch,...</p><p> \r\nCác món có thể chế biến từ Rau Muống:.</p><p>\r\n- Rau muống không chỉ là loại rau ăn kèm với các món phở bò, bún bò,... mà còn thích hợp để chế biến thành nhiều món ăn khác nhau từ luộc đến hấp, chiên, xào khi kết hợp với các loại thịt, hải sản, cá,...</p><p> \r\nMột số món ăn ngon từ rau muống mà bạn nên thử như rau muống xào tỏi chao lạ miệng, rau muống xào với ốc móng tay, canh ghẹ rau muống ngọt thanh,...</p>', 'raumuong.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(36, 'Su hào', 3, 9000, '<p>Su hào là một loại củ rất quen thuộc, được nhiều bà nội trợ chọn để chế biến thành các món ăn ngon cho gia đình. Xu hào rất giàu Vitamin, chất xơ, giúp chống oxy hóa mạnh, răng và nướu khỏe mạnh. Xu hào có thể chế biến thành các món ăn ngon như: xào, nấu canh, muối chua,...</p><p> \r\nSu hào là loại củ được sử dụng nhiều trong chế biến món ăn vì nó có vị ngọt nhẹ, giòn và rất giàu Vitamin, chất xơ. Trong su hào rất giàu Vitamin C. Loại Vitamin này có tính chống oxy hóa mạnh, giúp cơ thể duy trì các mô liên kết, răng và nướu khỏe mạnh, giúp bảo vệ cơ thể con người hạn chế ung thư và đẩy các gốc tự do có hại ra khỏi cơ thể…Su hào còn chứa một lượng dồi dào nhóm Vitamin B phức hợp, \r\nlà các yếu tố kết hợp với enzym trong quá trình trao đổi chất khác nhau trong cơ thể.</p><p>\r\n- Các món có thể chế biến từ Su Hào:.</p><p>\r\n- Ăn hết nồi cơm cùng thịt kho su hào.</p><p>\r\n- Su hào, cà rốt muối đơn giản ngon miệng.</p><p>\r\n- Nộm su hào, cà rốt giòn giòn.</p><p>\r\n- Su hào xào thịt bò đậm đà.</p>', 'suhao.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(37, 'Su su', 3, 11000, '<p>Su su hay su le trong phương ngữ miền Trung Việt Nam là một loại cây lấy quả ăn, thuộc họ Bầu bí, cùng với dưa hấu, dưa chuột và bí. Cây này có lá rộng, thân cây dây leo trên mặt đất hoặc trên giàn. \r\nỞ Việt Nam, su su được trồng để vừa lấy quả và vừa lấy ngọn trong chế biến các món ăn.</p><p>\r\nCác món có thể chế biến từ Su Su:.</p><p>\r\n- Su su xào nấm tuyết (mộc nhĩ trắng) Su su•Nấm mèo (mộc nhĩ)•Nấm tuyết (mộc nhĩ trắng, tuyết nhĩ)•Cà rốt.</p><p>\r\n- Su Su Xào Thịt Bò.</p><p>\r\n- Đọt su su xào giá.</p><p>\r\n- Su Su Xào Trứng Muối.</p><p>\r\n- Su Su xào trứng.</p><p>\r\n- Canh su su, cà rốt, khoai lang tím nấu thịt băm.</p><p>\r\n- Su su xào thịt heo.</p><p>\r\n- Su su xào trứng.</p>', 'susu.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(38, 'Tắc', 3, 10000, '<p>Tắc trái của Green Food luôn giữ được độ tươi mỗi ngày, được nuôi trồng theo quy trình công nghệ nghiêm ngặt, bảo đảm các chỉ tiêu về an toàn và chất lượng. Hương tắc thơm, vị chua dịu,\r\nkhông quá gắt kích thích vị giác của người dùng thường được dùng ăn kèm với các món bún, pha nước tắc.</p><p>\r\nTắc là loại quả có vị chua và mùi thơm kích thích. Tắc không chỉ được làm thức uống giải nhiệt và nguyên liệu chế biến món ăn, nước chấm mà công dụng của trái tắc còn chữa bệnh cực hay.</p><p> \r\nTrái tắc chứa nhiều vitamin A, C, B1,…cùng các khoáng chất như canxi, photpho, sắt,… Không chỉ có hương vị thơm ngon, quả tắc còn chứa nhiều dưỡng chất, đặc biệt là vitamin C – chất chống oxy hóa. Hơn nữa, quả tắc chứa rất ít natri và hầu như không có chất béo cũng như cholesterol.</p>', 'tac.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(39, 'Rau tần ô', 3, 7500, '<p>Rau tần ô hay còn gọi là rau cải cúc, là một loại rau có tính hàn mát, vị ngọt nhẹ, rất phù hợp cho việc chế biến thành các món canh rau cho gia đình. Ngoài ra, rau tần ô cũng mang đến nhiều lợi ích cho sức khỏe con người như trị ho, trị đau đầu, lợi tiểu, chữa tiêu chảy,...</p><p>\r\nCác món có thể chế biến từ Rau Tần Ô:.</p><p>\r\n- Rau tần ô khi được nấu cùng với các loại thịt heo, tôm,.. sẽ cho ra những món canh thơm ngon, ngọt thanh và bổ dưỡng như canh rau tần ô nấu với thịt bằm, canh rau tần ô thịt bò,...</p><p>\r\n- Rau tần ô cũng được dùng để chế biến thành các món xào như rau tần ô xào tỏi, xào thịt bò,...</p>', 'tano.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(40, 'Xà lách lô lô', 3, 13400, '<p>Được trồng theo hình thức thủy canh, Xà lách lô lô thủy canh trông rất tươi mát, có vị ngon ngọt, lá không quá giòn và cũng không quá mềm. Đặc biệt, bởi vì được nuôi trồng bởi quy trình nghiêm ngặt nên đảm bảo về chất lượng và an toàn đến tay người tiêu dùng.</p><p>\r\nXà lách từ lâu đã thành loại rau xanh ăn kèm không thể thiếu đối với nhiều món ăn, nhất là những món chiên, nướng. Ngoài ra với hàm lượng chất dinh dưỡng cao cũng như các ưu điểm nổi bật trong việc hỗ trợ cải thiện vóc dáng, sắc đẹp, xà lách luôn là loại rau chắc chắn phải có trong nhiều loại salad trộn hay cũng như nước ép.\r\nTrên thị trường hiện nay có rất nhiều loại xà lách, đặc điểm nhận dạng của lá xà lách thường là phiến lá to và có màu xanh,  nhưng mỗi loại xà lách lại có một điểm riêng biệt như xà lách búp mỡ là loại xà lách có lá lớn và được sắp xếp hơi rời rạc, dễ dàng tách ra từ thân của nó, xà lách lô lô cũng có lá sắp xếp rời rạc nhưng tầng lá rộng và xoăn.\r\nMàu xanh của lá trông rất tươi mát, có vị ngon ngọt, lá không quá giòn và cũng không quá mềm.</p>', 'xalachlolo.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(41, 'Xà lách búp', 3, 15500, '<p>Xà lách búp mỡ thủy canh của Bách Hóa Xanh có lá cây rất mềm, có vị ngọt thanh, rất thơm ngon khi ăn sống. Chứa nhiều chất xơ có lợi cho tiêu hóa, giàu giá trị dinh dưỡng, chứa nhiều vitamin và khoáng chất, có công dụng bồi bổ sức khỏe, chống oxy hóa, ngăn ngừa ung thư.</p><p>\r\nCác món có thể chế biến từ Xà lách búp:.</p><p>\r\nXà lách mỡ có thể dùng để chế biến nhiều món ăn ngon như luộc, canh, xào, salad.</p><p>\r\n- Làm rau sống, dùng để cuốn và ăn kèm với cá, thịt.</p><p>\r\n- Xà lách trộn dầu giấm, trứng.</p><p>\r\n- Xà lách trộn thịt bò.</p><p>\r\n- Xà lách chấm nước sốt cà.</p><p>\r\n- Ăn kèm chung với các món nhúng giấm.</p>', 'xalach.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(42, 'Cam Valencia', 2, 75000, '<p>Cam vàng Valencia trái tròn căng bóng, mọng nước và cực kỳ thơm ngon, đây là loại trái cây nhập khẩu từ Úc an toàn vệ sinh. Thích hợp để vắt uống bởi chứa nhiều vitamin C và vị chua dịu hợp khẩu vị.</p><p>\r\nCác món có thể chế biến từ Cam Valencia:</p><p>\r\n- Cam Vắt.</p><p>\r\n- Bổ cam ra thành từng múi.</p><p>\r\n- Nước ép cam.</p>', 'cam.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(43, 'Táo mỹ', 2, 80000, '<p>Là loại trái cây nhập khẩu xuất xứ từ Mỹ, nhiều giá trị dinh dưỡng. Loại táo này có kích thước vừa tay cầm, vỏ mỏng màu đỏ thẫm, phần thịt màu trắng, giòn cứng, vị ngọt mát xen kẽ một chút vị chua, rất dễ ăn. Có thể chế biến thành nhiều món ăn hoặc thức uống khác nhau, mang đến lợi ích sức khoẻ con người...</p>', 'tao.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(44, 'Chanh dây', 2, 18000, '<p>Chanh dây là loại trái cây có vị chua nhưng hậu ngọt, thường dùng làm bánh hay nước giải khát thanh nhiệt trong mùa hè nóng bức, là thức uống được nhiều người yêu thích. Ngoài là nước giải khát thì chanh dây còn có nhiều lợi ích cho sức khỏe. Loại trái này còn giàu các chất hữu cơ như caroten và polyphenol.\r\nTrên thực tế, khoa học đã chứng minh rằng trong các loại trái cây nhiệt đới như chuối, xoài, đu đủ, dứa, vải thiều thì chanh dây chứa hàm lượng polyphenol cao nhất. Ngoài ra còn chứa một lượng nhỏ chất sắt. Thông thường cơ thể chúng ta khó có thể hấp thụ chất sắt từ thực vật, tuy nhiên nhờ hàm lượng vitamin C cao trong trái chanh dây mà cơ thể dễ dàng hấp thụ chất sắt hơn.</p><p>\r\n- Các món có thể chế biến từ Chanh dây;.</p><p>\r\n- Sinh tố chanh dây ngon ngất ngây.</p><p>\r\n- Pha nước chanh dây mát lạnh.</p><p>\r\n- Canh chua chanh dây lạ miệng.</p><p>\r\n- Cuối tuần healthy với salad thanh long chanh dây.</p><p>\r\n- Cá dứa chiên giòn sốt chanh dây đơn giản.</p>', 'chanhday.jpeg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(45, 'Dâu tây New Zealand', 2, 82000, '<p>Là dâu tây được nhập khẩu từ New Zealand có vị chua, ngọt có thể ăn trực tiếp hoặc làm sinh tố, nước ép, kem,... Sản phẩm tươi ngon - an toàn thực phẩm người tiêu dùng, chứa nhiều vitamin A giúp tăng sức đề kháng cho cơ thể và ngăn ngừa nhiều bệnh.</p><p>\r\nDâu tây New Zealand có vị chua, ngọt có thể ăn trực tiếp hoặc làm sinh tố, nước ép, kem,… Loại trái cây nhập khẩu này chứa nhiều vitamin A, B1, B2, đặc biệt là vitamin C rất cao, hơn cả cam và dưa hấu, giúp tăng sức đề kháng cho cơ thể và ngăn ngừa nhiều bệnh. Chúng được ví như những viên đá quý màu đỏ này rất tốt cho trái tim của bạn theo những cách khác nhau.</p><p> \r\nCác món có thể chế biến từ Dâu tây:.</p><p>\r\n- Mứt dâu tây.</p><p>\r\n- Sinh tố dâu tây.</p><p>\r\n- Trà dâu.</p><p>\r\n- Trà sữa dâu.</p><p>\r\n- Kem dâu tây.</p><p>\r\n- Nước ép dâu tây.</p><p>\r\n- Cháo yến mạch dâu tây.</p><p>\r\n- Bánh kem dâu tây.</p>', 'dautay.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(46, 'Lê Nam Phi', 2, 72000, '<p>Lê nhập khẩu 100% từ Nam Phi (giấy chứng nhận nguồn gốc xuất xứ). Đạt tiêu chuẩn xuất khẩu toàn cầu. Bảo quản tươi ngon đến tận tay khách hàng. Quả hình chuông, tròn và thon đều. Vỏ màu xanh xen lẫn vàng và đỏ rực rỡ. Trái chín: chắc thịt, mọng nước, ngọt dịu, thơm mát.</p><p>\r\nLê Nam Phi giòn, ngọt, mọng nước, là loại trái cây nhập khẩu rất được yêu thích, lê Nam Phi bổ sung nhiều chất xơ, vitamin và khoáng chất cho cơ thể khoẻ và da đẹp, dáng chuẩn hơn.</p>', 'le.jpeg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(47, 'Ổi nữ hoàng', 2, 20000, '<p>Giống ổi này có vị ngọt man mát, hơi xốp, giòn, thơm nhè nhẹ, đặc biệt rất ít hạt cho cảm giăc ăn vô cùng thích thú. Trong ổi chứa nhiều vitamin giúp tăng cường miễn dịch, đẹp da, giảm cân,...Cam kết bán đúng khối lượng, chất lượng và an toàn, bao bì kín đáo sạch sẽ vệ sinh.</p><p>\r\nCác món có thể chế biến từ Ổi:.</p><p>\r\n- Bổ để ăn.</p><p>\r\n- Nước ép ổi.</p><p> \r\n- Sinh tố ổi.</p>', 'oi.jpeg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(48, 'Xoài', 2, 20500, '<p>Loại trái cây phổ biến được ưa chuộng giàu chất xơ, vitamin, khoáng chất thiết yếu giúp cung cấp chất dinh dưỡng cho cơ thể con người và mang lại nhiều lợi ích tuyệt vời cho hệ tiêu hóa, tim mạch, giúp mắt sáng, làm đẹp da. Xoài đeo có vị ngọt thanh nhẹ nhàng, giòn xốp được nhiều người ưa thích.</p><p>\r\nXoài đeo hạt lép là một loại trái cây gây sốt ở mọi nơi vì mùi vị ngọt thanh nhẹ nhàng, giòn xốp nhưng giá lại rất rẻ. Loại xoài này có dáng thon thắt eo ở gần đuôi, phần thân khá tròn, đầy nhưng vẫn có đường cong.</p><p>\r\nKhi còn sống sẽ có vỏ màu xanh nhạt, khi già sẽ chuyển dần sang xanh đậm. Khi chín xoài sẽ ngả sang màu vàng dần từ cuống xuống đuôi quả. Khi ăn quả còn xanh, bạn sẽ cảm nhận vị ngọt đậm nhưng không gắt cổ, giòn xốp, thanh mát và khi quả chín hẳn thì thịt sẽ mềm, ngọt đậm, hương thơm nồng nàn, lan toả.</p>', 'soai.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(49, 'Thịt heo ba rọi', 1, 96000, '<p>Ba rọi heo của thương hiệu CP đạt các tiêu chuẩn về an toàn toàn thực phẩm, đảm bảo chất lượng, độ tươi ngon. Thịt heo mềm, vân nạc, mỡ rõ ràng nên rất phù hợp làm nguyên liệu để nấu thịt kho hột vịt, thịt nướng BBQ. Có thể dùng điện thoại quét mã QR trên tem sản phẩm để kiểm tra nguồn gốc.</p><p>\r\nCác món có thể chế biến từ Thịt ba rọi:.</p><p>\r\n- Thịt luộc, cà pháo.</p><p>\r\n- Nướng.</p>', 'baroi.jpeg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(50, 'Chân giò', 1, 65000, '<p>Chân giò heo trước CP đạt các tiêu chuẩn về an toàn toàn thực phẩm. Giò heo săn chắc, thịt có sự kết hợp với gân mỡ nên ăn béo ngậy và thơm, thích hợp để hầm canh, nấu các món nước như hủ tiếu, bánh canh,... Có thể dùng điện thoại quét mã QR trên tem sản phẩm để kiểm tra nguồn gốc.</p><p>\r\nChân giò heo trước CP được sản xuất từ hệ thống được kiểm soát chặt chẽ theo nguyên tắc chuỗi khép kín “Thức ăn chăn nuôi - Trang trại chăn nuôi – Nhà máy chế biến thực phẩm”.</p><p>\r\nCác món được chế biến từ Chân giò:.</p><p>\r\n- Chân giò tàu xì Móng giò heo, ngũ vị hương, hành tím, tỏi, gừng, ớt tươi, ớt bột, hạt tiêu, quế hồi.</p><p>\r\n- Chân giò giả cầy.</p><p>\r\n- Chân giò hầm coca.</p><p>\r\n- Chân giò nấu giả cầy.</p><p>\r\n- Chân giò heo hầm củ cải muối.</p><p>\r\n- Chân giò heo hầm rau củ (cà rốt, khoai tây, bắp cải).</p><p>\r\n- Canh rau củ hầm chân giò.</p><p>\r\n- Chân giò nướng mộc.</p>', 'changio.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(51, 'Móng giò', 1, 74000, '<p>Móng giò heo của thương hiệu CP được đóng gói và bảo quản theo những tiêu chuẩn nghiêm ngặt về vệ sinh và an toàn thực phẩm, đảm bảo về chất lượng, độ tươi và ngon của thực phẩm, xuất xứ rõ ràng.Lớp da giòn, béo kết hợp với gân mềm nên thường được sử dụng để hầm các loại canh bổ dưỡng.</p><p>\r\nCác món được chế biến từ Móng giò:.</p><p>\r\n- Bún móng giò.</p><p>\r\n- Móng giò hầm táo đỏ.</p><p>\r\n- Móng giò om hoa hồi.</p>', 'monggio.jpeg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(52, 'Thịt sườn cốt', 1, 82000, '<p>Nạc dăm heo của thương hiệu Greenfeed được đóng gói và bảo quản theo những tiêu chuẩn nghiêm ngặt về vệ sinh và an toàn thực phẩm, đảm bảo về chất lượng, độ tươi và ngon của thực phẩm, xuất xứ rõ ràng. Thịt nạc dăm sạch sẽ, mềm, có độ đàn hồi nên rất phù hợp với món thịt luộc với bánh tráng.</p><p>\r\nCác món được chế biến từ Thịt nạc dăm:.</p><p>\r\n- Thịt nạc dăm chiên giòn Hàn Quốc.</p><p>\r\n- Thịt nạc dăm nướng (sườn nướng cơm tấm).</p><p>\r\n- Thịt nạc dăm kho.</p><p>\r\n- Thịt nạc dăm xào dứa.</p><p>\r\n- Bún thịt xào.</p><p>\r\n- Thịt heo cuộn phô mai.</p><p>\r\n- Đậu hũ và thịt nạc dăm băm.</p><p>\r\n- Bún chả Hà Nội.</p>', 'nacdam.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(53, 'Thịt nạc dăm', 1, 80000, '<p>Thịt cốt lết thường được cắt kèm với phần đầu xương sườn nên mọi người hay gọi là sườn cốt lết, là nguyên liệu quen thuộc của mọi nhà. Cốt lết heo nhập khẩu đông lạnh với phương pháp cấp đông hiện đại, giúp lưu giữ hương vị tự nhiên, mang đến những món ăn ngon cho gia đình.</p><p>\r\nCác món được chế biến từ Thịt Sườn Cốt\r\n- Sườn cốt lết rim mặn ngọt. Đứng đầu danh sách các món ngon dễ làm với thịt cốt lết sẽ là món sườn cốt lết rim mặn ngọt.</p><p>\r\n- Sườn cốt lết rim sữa tươi.</p><p>\r\n- Sườn cốt lết nướng cơm tấm.</p><p>\r\n- Sườn cốt lết rim nước dừa.</p><p>\r\n- Sườn cốt lết nướng mật ong.</p><p>\r\n- Sườn cốt lết sốt cà chua.</p><p>\r\n- Sườn cốt lết rim me.</p><p>\r\n- Sườn cốt lết áp chảo.</p>', 'suoncot.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(54, 'Thịt sườn non', 1, 109000, '<p>Sườn non CP đạt các tiêu chuẩn về an toàn toàn thực phẩm, đảm bảo chất lượng, độ tươi ngon. Sườn được cắt sẵn miếng vừa ăn, có thể chế biến thành nhiều món ngon như sườn kho tiêu, sườn nấu canh, sườn xào chua ngọt,..... Có thể dùng điện thoại quét mã QR trên tem sản phẩm để kiểm tra nguồn gốc.</p><p>\r\nCác món được chế biến từ Thịt Sườn Non:.</p><p>\r\n- Sườn rang muối.</p><p>\r\n- Sườn sốt me.</p><p>\r\n- Sườn nướng chao.</p><p>\r\n- Sườn non kho thơm (sườn non kho dứa).</p><p>\r\n- Sườn non nướng ngũ vị.</p><p>\r\n- Sườn nướng cà phê.</p><p>\r\n- Sườn rán giòn.</p><p>\r\n- Sườn chiên nước mắm.</p>', 'suonnon.jpeg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(55, 'Ức gà', 1, 43500, '<p>Thịt gà là một trong những thực phẩm được tiêu thụ thường xuyên trong đời sống hằng ngày. Trong đó, phần thịt được sử dụng nhiều trong chế độ ăn uống khoa học đó là ức gà. Ức gà phi lê CP là sản phẩm được sản xuất chọn lọc từ những con gà khoẻ mạnh trên dây chuyền nuôi hiện đại, được kiểm soát chặt chẽ theo nguyên tắc chuỗi khép kín “Thức ăn chăn nuôi - Trang trại chăn nuôi – Nhà máy chế biến thực phẩm”.</p><p>\r\nCác món được chế biến từ Ức gà:.</p><p>\r\nSalad ức gà.</p><p>\r\n- Ức gà xào nấm.</p><p>\r\n- Ức gà nướng.</p><p>\r\n- Ức gà sốt mật ong.</p><p>\r\n- Cải thảo cuộn ức gà.</p><p>\r\n- Ức gà xào bông cải xanh.</p><p>\r\n- Ức gà áp chảo.</p><p>\r\n- Ức gà cuộn phô mai.</p>', 'ucga.jpeg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(56, 'Cánh gà', 1, 83000, '<p>Cánh gà tươi nhập khẩu là một trong những bộ phận ngon nhất của thịt gà, thích hợp dùng cho các món chiên, xào, kho,... và rất nhiều nhiều người yêu thích. Cánh gà không chỉ có phần thịt mềm mà còn có lớp da dai dai, phù hợp với khẩu vị của nhiều người. \r\nCánh gà nhập khẩu đông lạnh từ Mỹ với phương pháp làm đông lạnh cấp tốc từ thịt gà tươi trong thời gian nhanh nhất, giúp các dinh dưỡng thất thoát sẽ ít hơn, chất lượng thịt không kém xa thịt gà.</p><p>\r\nCác món có thể chế biến từ Cánh gà:.</p><p>\r\n- Cánh gà chiên nước mắm đậm đà.</p><p>\r\n- Cánh gà chiên giòn rụm.</p><p>\r\n- Cánh gà chiên bơ ngon như mơ.</p><p>\r\n- Cánh gà nướng siêu ngon.</p><p>\r\n- Cánh gà sốt chua cay kiểu Thái.</p><p>\r\n- Lạ miệng với cánh gà chiên coca.</p>', 'canhga.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(57, 'Chân gà', 1, 62000, '<p>Chân gà của thương hiệu CP đạt các tiêu chuẩn về an toàn thực phẩm, đảm bảo về chất lượng, độ tươi và ngon, xuất xứ rõ ràng. Thích hợp để nấu món chân gà ngâm sả tắc, chân gà nướng sa tế, chân gà sốt cay Hàn Quốc,... Có thể dùng điện thoại quét code QR trên sản phẩm để kiểm tra nguồn gốc.</p><p>\r\nCác món được chế biến từ Chân gà:.</p><p>\r\n- Chân gà rang muối. Nguyên liệu làm chân gà rang muối (cho 4 người).</p><p>\r\n- Chân gà ngâm sả tắc.</p><p>\r\n- Chân gà sốt cay Hàn Quốc.</p><p>\r\n- Nộm chân gà rút xương.</p><p>\r\n- Chân gà ngâm giấm sả ớt.</p><p>\r\n- Chân gà nướng sa tế.</p><p>\r\n- Chân gà hấp tàu xì.</p><p>\r\n- Chân gà muối chua.</p>', 'changa.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(58, 'Đùi gà', 1, 52000, '<p>Đùi tỏi gà CP đạt các tiêu chuẩn về an toàn thực phẩm, đảm bảo về chất lượng, độ tươi và ngon, xuất xứ rõ ràng. Là một một nguyên liệu thích hợp để nấu món gà rán, cơm đùi gà quay tiêu hay gà chiên nước mắm,... Có thể dùng điện thoại quét code QR trên sản phẩm để kiểm tra nguồn góc.</p><p>\r\nThịt gà CP được sản xuất từ hệ thống được kiểm soát chặt chẽ theo nguyên tắc chuỗi khép kín “Thức ăn chăn nuôi - Trang trại chăn nuôi – Nhà máy chế biến thực phẩm”.</p><p> \r\nCác món được chế biến từ Đùi gà:.</p><p>\r\n- Đùi gà chiên.</p><p>\r\n- Đùi gà hấp cam.</p><p>\r\n- Đùi gà hấp xì dầu.</p><p>\r\n- Đùi gà nướng bơ tỏi.</p><p>\r\n- Đùi gà nướng muối ớt.</p><p>\r\n- Gà rim nước tương cà.</p><p>\r\n- Đùi gà chiên sốt chua ngọt.</p><p>\r\n- Đùi gà rim.</p>', 'duiga.jpeg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(59, 'Gà nguyên con', 1, 68000, '<p>Là một loại gia súc có rất nhiều dinh dưỡng.</p><p>\r\nCác món được chế biến từ Gà:.</p><p>\r\n- Gà hầm thuốc Bắc.</p><p>\r\n- Gỏi gá lá chanh chua ngọt.</p><p>\r\n- Gỏi gà bắp cải.</p><p>\r\n- Gà nướng mật ong.</p><p>\r\n- Gà kho măng.</p><p>\r\n- Cánh gà chiên nước mắm.</p><p>\r\n- Gà chiên xù</p><p>\r\n- Gà chiên xì dầu.</p>', 'ganguyencon.jpeg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(60, 'Cá nục', 4, 83000, '<p>Là loại cá mang đến nhiều dưỡng chất có lợi và công dụng tuyệt vời cho sức khoẻ. Cá nục đông lạnh vẫn giữ được độ tươi ngon từ cá biển tươi, giúp bảo quản lâu hơn mà thịt cá vẫn chắc, không bở mà vẫn giữ nguyên vị đậm đà cho việc chế biến món ăn.</p><p>\r\nCác món có thể chế biến từ Cá Nục:.</p><p>\r\n- Chiên ngập dầu.</p><p>\r\n- Sốt cà chua.</p>', 'canuc.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(61, 'Cá Basa', 4, 69000, '<p>Cá basa với đặc tính ít xương khi chế biến thịt sẽ có vị ngọt thơm và ăn rất ngon, dễ dàng dùng làm nguyên liệu chế biến các món ngon và chứa nhiều chất dinh dưỡng. Cá saba đông lạnh giúp bảo quản lâu hơn mà thịt cá vẫn chắc, không bở mà vẫn giữ nguyên vị đậm đà.</p><p>\r\nCá saba (cá sapa) là một loài cá nổi tiếng có nguồn gốc từ Nhật Bản, có màu trắng, nhiều nạc và ít mỡ, thịt cá dày, ít xương khi chế biến thịt sẽ có vị ngọt thơm và ăn rất ngon. Cá saba đông lạnh được lấy từ những con cá tươi, còn sống, thịt săn chắc, làm lạnh nhanh và trữ đông ở -18 độ C,\r\nđiều này giúp cho thịt cá vẫn giữ nguyên độ tươi ngay khi rã đông sử dụng. Cá đông lạnh tại Green Food có thể bảo quản trong thời gian dài mà vẫn đảm bảo hương vị cũng như giá trị dinh dưỡng, thích hợp với nhu cầu sử của các bà nội trợ cho nhiều lần sử dụng.</p><p>\r\nCác món có thể chế biến từ Cá Saba:.</p><p>\r\n- Cá saba nướng giấy bạc.</p><p>\r\n- Cá saba kho tộ.</p><p>\r\n- Cá saba áp chảo.</p><p>\r\n- Cá saba sốt cà chua.</p><p>\r\n- Cá saba kho măng.</p>', 'casaba.jpeg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(62, 'Cá trứng', 4, 38000, '<p>Hải sản đông lạnh thương hiệu Đôi Đũa Vàng. Loài cá có kích thước nhỏ, bên trong chứa đầy ắp trứng có hương vị bùi béo và giàu dinh dưỡng. Cá trứng Đôi Đũa Vàng khay 400g từ nguồn nguyên liệu nhập khẩu châu Âu có thể chế biến nhiều món ăn thơm như chiên giòn, nướng muối ớt hay sốt cà chua,...</p><p>\r\nCác món có thể chế biến từ Cá trứng:.</p><p>\r\n- Cá trứng chay sốt cà chua. Bã đậu mới •Tàu hũ ki lá, lá rong biển cuộn sushi•cà chua, hạt nêm•đường.</p><p>\r\n- Cá Trứng Lăn Bột Chiên Giòn. cá trứng•Bột chiên giòn•Dầu ăn•Tương ớt.</p><p>\r\n- Cá Trứng Chiên Mắm.</p><p>\r\n- Cá trứng chiên giòn.</p><p>\r\n- Cá Trứng Chiên Mè Chấm Mắm Me.</p><p>\r\n- Cơm chiên cá trứng.</p><p>\r\n- Cá trứng chiên sả ớt.</p><p>\r\n- Cá trứng chiên.</p>', 'catrung.jpeg', '2022-06-02 22:48:55', '2022-06-02 22:48:55'),
(64, 'Lườn cá hồi', 4, 32500, '<p>Là loại thực phẩm giàu chất dinh dưỡng và cực kỳ tốt cho sức khoẻ. Lườn cá hồi vừa dễ ăn, vừa mang vị ngậy béo vô cùng, lại giàu omega-3, protein,... tốt cho cơ thể. Lườn cá hồi tươi ngon,\r\nđược làm sạch cẩn thận, chỉ cần rửa sạch là có thể chế biến món ăn. Không chỉ ngon miệng mà cá hồi còn được biết đến là thực phẩm giàu dinh dưỡng và tốt cho sức khỏe. Ăn cá hồi giúp phòng tránh một số bệnh và đặc biệt có ích cho tim, da, tóc và não bộ con người.\r\nCá hồi là loại cá có chứa Omega-3 giàu EPA và DHA, protein cùng nhiều dưỡng chất thiết yếu khác như vitamin B, kali và selen,... đều là những dưỡng chất cần thiết cho cơ thể. Nếu muốn sở hữu làn da trắng sáng và mịn màng hơn ngay từ bên trong thì cá hồi chính là sự lựa chọn tốt nhất cho bạn.\r\nCác chất chống ôxy hóa carotenoid trong cá hồi có thể làm giảm đáng kể ảnh hưởng của các gốc tự do gây hại, gây lão hóa cho da. Lườn cá hồi nằm ở phần bụng cá, có vị béo, mềm, được mọi người rất yêu thích.</p>', 'luoncahoi.jpg', '2022-06-02 22:48:55', '2022-06-02 22:48:55');

